import React, { Component } from 'react';
import axios from 'axios'; 
import TableRow from './accountsTableRow';


export default class listLogins extends Component {
    constructor(props) {
        super(props);
        this.state = {accounts: []};
      }

    abortController = new AbortController();

    componentDidMount(){
        fetch(axios.get('http://localhost:4000/admin/'), { signal: this.abortController.signal})
          .then(response => {
            this.setState({ accounts: response.data });
            })
            .catch(function (error) {
              if(error.name == 'AbortError')
            console.log(error);
        })
    }

    componentDidUpdate() {
        axios.get('http://localhost:4000/admin')
        .then(response => {
        this.setState({ accounts: response.data });
            })
            .catch(function (error) {
            console.log(error);
        })
    }
    
    tabRow(){
      return this.state.accounts.map(function(object, i){
          return <TableRow obj={object} key={i} />;
      });
    }

    componentWillUnmount() {
      
    }
  
      render() {
        return (
          <div>
            <h3 align="center">List of Accounts</h3>
            <table className="table table-striped" style={{ marginTop: 20 }}>
              <thead>
                <tr>
                  <th>Id:</th>
                  <th>Name:</th>
                  <th>Email</th>
                  <th>Password</th>
                  <th>Date created</th>
                  <th colSpan="2">Action</th>
                </tr>
              </thead>
              <tbody>
                { this.tabRow() }
              </tbody>
            </table>
          </div>
        );
      }
}
